def check_unique(block: str):
    for position, c in enumerate(block):
        if position+1 < len(block):
            if c in block[position+1:]:
                return False
    return True

def find_start_of_message(stream: str):
    return find_unique_of_len(stream, 14)

def find_start_of_packet(stream: str):
    return find_unique_of_len(stream, 4)

def find_unique_of_len(stream: str, length):
    for x in range(length,len(stream)):
        if check_unique(stream[x-length:x]):
            return x

